    public static String convertHtmlToJson(String html) {
        Document doc = Jsoup.parse(html);
        Elements indexLists = doc.select(".at-indexes__list");
        List<Map<String, Object>> list = new ArrayList<>();

        for (Element indexList : indexLists) {
            String title = indexList.select(".at-indexes__list-title").text().trim();
            Elements items = indexList.select(".item-content__info-title");

            Map<String, Object> map = new HashMap<>();
            map.put("title", title);
            map.put("key", title);

            List<Map<String, Object>> subList = new ArrayList<>();
            for (Element item : items) {
                Map<String, Object> subMap = new HashMap<>();
                subMap.put("name", item.text().trim());
                subList.add(subMap);
            }

            map.put("items", subList);
            list.add(map);
        }

        return JSON.toJSONString(list);
    }
